import time
import random

def print_woody_quote():
    # Woody Allen style quote
    quote = "I'm not neurotic, I'm just highly sensitive to the absurdity of existence and the futility of human striving. Also, I'm probably just confusing myself."
    
    # Color codes
    colors = [
        '\033[91m',  # Red
        '\033[92m',  # Green
        '\033[93m',  # Yellow
        '\033[94m',  # Blue
        '\033[95m',  # Magenta
        '\033[96m',  # Cyan
    ]
    
    reset = '\033[0m'
    
    # Create decorative border
    border = "═" * (len(quote) + 4)
    
    # Print animated border
    print()
    for i in range(len(border)):
        print(f"\r{colors[i % len(colors)]}{border[0:i]}{reset}", end="", flush=True)
        time.sleep(0.01)
    
    print()
    
    # Print quote with color variation
    colored_quote = ""
    for i, char in enumerate(quote):
        if char.isalpha():
            colored_quote += f"{colors[(i + 3) % len(colors)]}{char}{reset}"
        else:
            colored_quote += char
    
    print(f"{colors[2]}╔{border}╗{reset}")
    print(f"{colors[2]}║{reset} {colored_quote} {colors[2]}║{reset}")
    print(f"{colors[2]}╚{border}╝{reset}")
    
    # Print whimsical ASCII art
